			import sailpoint.object.Identity;
			import sailpoint.object.Capability;
			import sailpoint.object.ResourceObject;
			// change the name to a combination of firstname and lastname
			String employeeType = account.getStringAttribute("employeeType");
			String sAMAccountName = account.getStringAttribute("sAMAccountName");
			String userDN = account.getStringAttribute("distinguishedName");
			userDN= userDN.toLowerCase();

			
			String identityName = account.getStringAttribute("msDS-PrincipalName");
			String name = identityName;
			if(employeeType == null  ) employeeType = "Orphan";
			else if(employeeType.equalsIgnoreCase("admin")|| employeeType.equalsIgnoreCase("others") || employeeType.equalsIgnoreCase("TEMPLATE_ACCOUNT")|| employeeType.equalsIgnoreCase("TEST_ACCOUNT") || employeeType.equalsIgnoreCase("SERVICE_ACCOUNT"))  employeeType ="Orphan-"+ employeeType;
			else if(employeeType.equalsIgnoreCase("Element ACCOUNT"))   employeeType ="Orphan-"+ employeeType;
			identity.setAttribute("identityType",employeeType);
			identity.setName(name);
