import sailpoint.api.IdentityService;
import sailpoint.api.SailPointContext;
import sailpoint.object.Identity;
import sailpoint.object.*;
import sailpoint.tools.*;
String applicationName = "Element AD";
String value = null;
Link primaryLink = getPrimaryApplicationLink(context, identity, applicationName);
if(primaryLink != null){
   Object newValue =primaryLink.getAttribute("departmentNumber");
   if(newValue instanceof List){
   		List newList = (List) newValue;
   		for(String newListVal:newList){
   			if(value == null) value = newListVal;
   			else	value= value +"," +newListVal;
   		}
   }else{
   		value= (String) newValue;
   }
}

String applicationName2 = "CEI AD";
if(value == null){
	Link cei_primaryLink = getPrimaryApplicationLink(context, identity, applicationName2);
	if(cei_primaryLink != null){
	   Object newValue =cei_primaryLink.getAttribute("departmentNumber");
	   if(newValue instanceof List){
	   		List newList = (List) newValue;
	   		for(String newListVal:newList){
	   			if(value == null) value = newListVal;
	   			else	value= value +"," +newListVal;
	   		}
	   }else{
	   		value= (String) newValue;
	   }
	}
}

return value;
