// Imports
import sailpoint.object.*;
// Making a BIG assumption here that the nameid-format is unspecified/persitent
System.out.println("assertionAttributes" + assertionAttributes);
// Get the nameId from the assertionAttributes
String nameId = (String)assertionAttributes.get("nameId");
System.out.println("nameId" + nameId);
Identity ident;
if(nameId != null) {
	// Lookup the identity based on nameId
	QueryOptions qo= new QueryOptions();
	qo.addFilter(Filter.or(Filter.eq("application.name", "CEI AD"),Filter.ignoreCase(Filter.eq("application.name", "Element AD"))));
	qo.addFilter(Filter.ignoreCase(Filter.eq("emailAddress", nameId)));
	Iterator iter = context.search(Link.class, qo,"id");
	while (iter.hasNext()) {
		found=true;
		Object[] row = (Object[]) iter.next();
		String id = (String) row[0];
		Link adLink = context.getObjectById(Link.class,id);
		String idName= adLink.getIdentity().getName();
		ident = adLink.getIdentity();
	}
}
return ident;
